<?php

namespace VictorRayan\DropboxRayanVrsrb;

class getEnvDropboxTokens{


    public function app_key(){

        $app_key = $_ENV['DROPBOX_APP_KEY'];
        return $app_key;
    }

    public function app_secret(){

        $app_secret = $_ENV['DROPBOX_APP_SECRET'];
        return $app_secret;
    }

    public function app_refresh_token(){

        $app_refresh_token = $_ENV['DROPBOX_REFRESH_TOKEN'];
        return $app_refresh_token;


    }
}



?>